var inHex = require('../../format/input').inHex;
var Web3 = /** @class */ (function () {
    function Web3(provider) {
        this._provider = provider;
    }
    Web3.prototype.clientVersion = function () {
        return this._provider
            .send('web3_clientVersion');
    };
    Web3.prototype.sha3 = function (hexStr) {
        return this._provider
            .send('web3_sha3', inHex(hexStr));
    };
    return Web3;
}());
module.exports = Web3;
